def insertionSort(arr):
    shift = 0
    for i in range(1, n):
        x = int(arr[i])
        for j in range(i):
            if j >= 0 and int(arr[j]) > x:
                arr[i], arr[j] = arr[j], arr[i]
                shift += 1
    return shift

